package by.academy.lesson3;

public class DoubleSum {

	public static void main(String [] args) {
		// TODO Auto-generated method stub
		double b = 0;
		for(int i = 0; i < 10; i++) {
			b = b + 0.1;
			System.out.println(b);
		}
		
		System.out.println("Result: ");
		System.out.println(b);



	}

}
